import { ReactNode, useState } from 'react';
import ApiContext from './ApiContext';
import axios from 'axios';
import {IBeneficiare } from './types';
import { IForfait } from './types';

interface IContextProviderProps {
  children: ReactNode;
}

const ContextProvider = ({ children }: IContextProviderProps) => {
  const [loading, setLoading] = useState(false);
  const [beneficaires, setBeneficaires] = useState<IBeneficiare[]>([]);
  const [forfaits, setForfaits]= useState<IForfait[]>([]);

  const getBeneficiaires = async ({
    nom,
    prenom,
    matricule,
  }: {
    nom: string;
    prenom: string;
    matricule: string;
  }): Promise<IBeneficiare[]> => {
    setLoading(true);
    try {
      const response = await axios.get(
        'http://localhost:8089/beneficiaire/recherche?nom=' + nom,
      );
      console.log(response.data);
      setBeneficaires(response.data as IBeneficiare[]);
      setLoading(false);
      return response.data as IBeneficiare[];
    } catch (error) {
      console.log(error);
      setLoading(false);
      return []; 
    }
  };
  const getForfaits = async ({
    nomForfait,
    soldeData,
    soldeAppels,
    montant,
  }: {
    nomForfait: string;
    soldeData: string;
    soldeAppels: string;
    montant: string;
  }): Promise<IForfait[]> => {
    setLoading(true);
    try {
      const response = await axios.get(
        'http://localhost:8089/forfaits/rechercheForfait',
      );
      console.log(response.data);
      setForfaits(response.data as IForfait[]);
      setLoading(false);
      return response.data as IForfait[];
    } catch (error) {
      console.log(error);
      setLoading(false);
      return []; 
    }
  };

  const addBeneficiaire = async ({
    nom,
    prenom,
    matricule,
    centreCout,
    rfDirection,
    rfBeneficiaire,
  }: {
    nom: string;
    prenom: string;
    matricule: string;
    centreCout: number;
    rfDirection: number;
    rfBeneficiaire: number;
  }): Promise<IBeneficiare[]> => {
    setLoading(true);
    try {
      console.log('GGGGGGGGGG' + rfDirection);
      const response = await axios.post(
        'http://localhost:8089/beneficiaire',
        {
          nom: nom,
          prenom: prenom,
          matricule: matricule,
          rfDirection: {id: rfDirection},
          rfBeneficiaire: {id: rfBeneficiaire},
          centreCout: {id: centreCout},
        }
      );
      console.log(response.data);
      setBeneficaires(response.data as IBeneficiare[]);
      setLoading(false);
      return response.data as IBeneficiare[];
    } catch (error) {
      console.log(error);
      setLoading(false);
      return []; 
    }
  };

  const apiContextValue = {
    getBeneficiaires,
    addBeneficiaire,
    getForfaits,
    forfaits: forfaits,
    beneficaires: beneficaires,
    loading,
  };

  return (
     <ApiContext.Provider value={apiContextValue}>
      {children}
    </ApiContext.Provider>
  );
};

export default ContextProvider;
