  useEffect(() => {
    fetchBeneficiaire();
  }, []);

  const fetchBeneficiaire = async () => {
    try {
      const response = await axios.put(`http://localhost:8089/beneficiaire/updateBeneficiaire2/{id}`);
      setBeneficiaire(response.data);
    } catch (error) {
      console.error('Erreur lors de la modification du bénéficiaire :', error);
    }
  };

   

