import { createContext } from 'react';
import { IBeneficiare } from './types';

interface IApiContext {
  getBeneficiaires: ({
    nom,
    prenom,
    matricule,
  }: {
    nom: string;
    prenom: string;
    matricule: string;
  }) => Promise<IBeneficiare[]>;

  addBeneficiaires: ({
    nom,
    prenom,
    matricule,
    centreCout,
    rfDirection,
    rfBeneficiaire,
  }: {
    nom: string;
    prenom: string;
    centreCout: string;
    matricule: string;
    rfDirection: string;
    rfBeneficiaire: string;
  }) => Promise<IBeneficiare[]>; // Updated property name to 'addBeneficiaires'
  
  loading: boolean;
  beneficaires: IBeneficiare[] | null;
}

const ApiContext = createContext<IApiContext>({
  getBeneficiaires: async () => [],
  addBeneficiaires: async () => [], // Updated property name to 'addBeneficiaires' 
  beneficaires: [],
  loading: false,
});

export default ApiContext;
