import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ModifierBeneficiaire = () => {
  const { id } = useParams();
  const [nom, setNom] = useState('');
  const [prenom, setPrenom] = useState('');
  // Ajoutez d'autres états pour les autres champs de saisie

  useEffect(() => {
    // Récupérez les informations du bénéficiaire à partir de votre API ou de votre source de données
    const fetchBeneficiaire = async () => {
      try {
        const response = await fetch(`http://localhost:8089/beneficiaire/updateBeneficiaire2/{id}`);
        const data = await response.json();
        // Mettez à jour les états avec les informations du bénéficiaire récupérées
        setNom(data.nom);
        setPrenom(data.prenom);
        // Mettez à jour les autres états avec les informations correspondantes
      } catch (error) {
        console.error('Erreur lors de la récupération du bénéficiaire :', error);
      }
    };

    fetchBeneficiaire();
  }, [id]);

  const handleNomChange = (event) => {
    setNom(event.target.value);
  };

  const handlePrenomChange = (event) => {
    setPrenom(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Envoyez une requête à votre API ou mettez à jour votre source de données avec les valeurs modifiées
    // Utilisez l'ID du bénéficiaire et les valeurs saisies dans les états
  };

  return (
    <div>
      <h1>Modifier le bénéficiaire</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Nom:
          <input type="text" value={nom} onChange={handleNomChange} />
        </label>
        <label>
          Prénom:
          <input type="text" value={prenom} onChange={handlePrenomChange} />
        </label>
        {/* Ajoutez d'autres champs de saisie pour les autres informations du bénéficiaire */}
        <button type="submit">Enregistrer les modifications</button>
      </form>
    </div>
  );
};

export default ModifierBeneficiaire;
